import React from 'react';
import {t} from '@oracle-cx-commerce/utils/generic';
import Alert from '@oracle-cx-commerce/react-components/alert';
import PropTypes from 'prop-types';

const CreateReturnSuccessMsg = props => {
  const {returnRequestId, alertReturnRequestSuccessMsg} = props;

  return (
    <div className="ReturnRequest__Success">
      <Alert type="success" message={t(alertReturnRequestSuccessMsg, {RETURNREQUESTID: returnRequestId})} />
    </div>
  );
};

CreateReturnSuccessMsg.propType = {
  /*Return Request id is the generated id after a successful return is placed */
  returnRequestId: PropTypes.string.isRequired
};

export default CreateReturnSuccessMsg;
