import {getPage, getReturnRequests, getMerchantSettings, getOrders} from '@oracle-cx-commerce/commerce-utils/selector';

export const getReturnData = state => {
  const {returnReasons} = getMerchantSettings(state);
  const {contextId} = getPage(state);
  const {returnRequestId} = getReturnRequests(state);
  const orders = getOrders(state);
  const orderData = orders[contextId];

  return {
    returnRequestId,
    returnReasons,
    contextId,
    orderData
  };
};
