import {
  getCurrentOrganizationId,
  getCurrentPriceListGroup,
  getOrganizations,
  getRolesForProfile
} from '@oracle-cx-commerce/commerce-utils/selector';

export const getComponentData = state => {
  const currentOrganizationId = getCurrentOrganizationId(state);
  const currentOrganization =
    getOrganizations(state) && currentOrganizationId ? getOrganizations(state)[currentOrganizationId] : undefined;

  const priceListGroup = getCurrentPriceListGroup(state);

  const currencyCode = priceListGroup.currency ? priceListGroup.currency.currencyCode : undefined;
  const roles = getRolesForProfile(state);

  return {
    currentOrganizationId,
    currentOrganization,
    currencyCode,
    roles
  };
};
