import {getOrder, getPage} from '@oracle-cx-commerce/commerce-utils/selector';

export const getOrderData = state => {
  const orderId = getPage(state).contextId;
  const order = getOrder(state, {id: orderId});

  return {
    orderId,
    order
  };
};
