import {getPage, getCurrentOrderId, getCurrentOrder} from '@oracle-cx-commerce/commerce-utils/selector';

export const getOrderData = state => {
  const orderId = getPage(state).contextId;
  const incompleteOrderId = getCurrentOrderId(state);
  const currentOrder = getCurrentOrder(state);

  return {
    orderId,
    incompleteOrderId,
    currentOrder
  };
};
