import Card from '@oracle-cx-commerce/react-components/card';
import ClipboardCheckIcon from '@oracle-cx-commerce/react-components/icons/clipboard-check';
import Link from '@oracle-cx-commerce/react-components/link';
import {PAGE_ORDER_PENDING_APPROVAL_LINK} from '@oracle-cx-commerce/commerce-utils/constants';
import PropTypes from 'prop-types';
/*
 ** Copyright (c) 2020 Oracle and/or its affiliates.
 */
import React from 'react';
import Styled from '@oracle-cx-commerce/react-components/styled';
import css from '@oracle-cx-commerce/react-widgets/profile/profile-orders-pending-approval-summary/components/orders-pending-approval-details/styles.css';

/**
 * Widget to display the orders pending approval summary details at profile page.
 * @param {Object} props the properties object
 */
const OrdersPendingApprovalDetails = props => {
  const {actionManage, textOrdersPendingApproval} = props;
  const {ordersPendingApproval} = props;

  return (
    <Styled id="OrdersPendingApprovalDetails" css={css}>
      <div className="OrdersPendingApprovalDetails__Card">
        <Card>
          <div className="OrdersPendingApprovalDetails__ApprovalDetails">
            <div className="OrdersPendingApprovalDetails__RowItem">
              <div className="OrdersPendingApprovalDetails__Icon">
                <ClipboardCheckIcon />
              </div>
              <div className="OrdersPendingApprovalDetails__Content">
                <span className="OrdersPendingApprovalDetails__TotalOrders">{ordersPendingApproval}</span>{' '}
                {textOrdersPendingApproval}
              </div>
            </div>
          </div>
          <div className="OrdersPendingApprovalDetails__Manage">
            <Link href={PAGE_ORDER_PENDING_APPROVAL_LINK}>{actionManage}</Link>
          </div>
        </Card>
      </div>
    </Styled>
  );
};

OrdersPendingApprovalDetails.propTypes = {
  /** Total number of pending orders approval */
  ordersPendingApproval: PropTypes.number
};

OrdersPendingApprovalDetails.defaultProps = {
  ordersPendingApproval: 0
};

export default OrdersPendingApprovalDetails;
