import React, {useContext} from 'react';
import {ContainerContext} from '@oracle-cx-commerce/react-ui/contexts';
import Styled from '@oracle-cx-commerce/react-components/styled';
import css from '@oracle-cx-commerce/react-widgets/profile/profile-purchase-list-summary-information/styles.css';
import {useDateFormatter} from '@oracle-cx-commerce/react-components/utils/hooks';
import {useComponentData} from '@oracle-cx-commerce/react-widgets/profile/profile-purchase-list-summary-information/selectors';

/**
 * Widget that displays basic purchase list related information
 * such as purchase list name, purchase list owner, purchase list description,
 * last modified date, last modified by, etc.
 */
const ProfilePurchaseListSummaryInformation = props => {
  //resources
  const {
    textPurchaseListOwner,
    textLastModified,
    textModifiedBy,
    textListSharedWithAccount,
    textListSharedWithEmail,
    textListNotShared,
    textListSharedWithBoth
  } = props;

  //container context
  const {
    id: purchaseListId,
    name: purchaseListName,
    owner = {},
    lastModifiedBy = {},
    lastModifiedDate,
    description
  } = useContext(ContainerContext);

  const {sharedWith, isListOwner} = useComponentData();

  const formatDate = useDateFormatter();

  let sharedWithInfo;

  if (!sharedWith.organizationSharingEnabled && !sharedWith.emailSharingEnabled) {
    sharedWithInfo = (
      <div className="ProfilePurchaseListSummaryInformation__Row">
        <div className="ProfilePurchaseListSummaryInformation__TextCol">
          <span className="ProfilePurchaseListSummaryInformation__Text">{textListNotShared}</span>
        </div>
      </div>
    );
  } else if (sharedWith.organizationSharingEnabled && sharedWith.emailSharingEnabled) {
    sharedWithInfo = (
      <div className="ProfilePurchaseListSummaryInformation__Row">
        <div className="ProfilePurchaseListSummaryInformation__TextCol">
          <span className="ProfilePurchaseListSummaryInformation__Text">{textListSharedWithBoth}</span>
        </div>
      </div>
    );
  } else if (sharedWith.organizationSharingEnabled) {
    sharedWithInfo = (
      <div className="ProfilePurchaseListSummaryInformation__Row">
        <div className="ProfilePurchaseListSummaryInformation__TextCol">
          <span className="ProfilePurchaseListSummaryInformation__Text">{textListSharedWithAccount}</span>
        </div>
      </div>
    );
  } else if (sharedWith.emailSharingEnabled) {
    sharedWithInfo = (
      <div className="ProfilePurchaseListSummaryInformation__Row">
        <div className="ProfilePurchaseListSummaryInformation__TextCol">
          <span className="ProfilePurchaseListSummaryInformation__Text">{textListSharedWithEmail}</span>
        </div>
      </div>
    );
  }

  return (
    <Styled id="ProfilePurchaseListSummaryInformation" css={css}>
      {purchaseListId && (
        <div className="ProfilePurchaseListSummaryInformation">
          <h1>{purchaseListName}</h1>
          {/* Purchase list owner */}
          <div className="ProfilePurchaseListSummaryInformation__Row">
            <div className="ProfilePurchaseListSummaryInformation__LabelCol">
              <span className="ProfilePurchaseListSummaryInformation__Label">{textPurchaseListOwner}</span>
            </div>
            <div className="ProfilePurchaseListSummaryInformation__ValueCol">
              <span className="ProfilePurchaseListSummaryInformation__Value">{`${owner.firstName} ${owner.lastName}`}</span>
            </div>
          </div>
          {/* Purchase list last modified information */}
          {isListOwner && (
            <>
              {/* Purchase list last modified date */}
              <div className="ProfilePurchaseListSummaryInformation__Row">
                <div className="ProfilePurchaseListSummaryInformation__LabelCol">
                  <span className="ProfilePurchaseListSummaryInformation__Label">{textLastModified}</span>
                </div>
                <div className="ProfilePurchaseListSummaryInformation__ValueCol">
                  <span className="ProfilePurchaseListSummaryInformation__Value">
                    {formatDate(new Date(lastModifiedDate))}
                  </span>
                </div>
              </div>
              {/* Purchase list last modified by */}
              <div className="ProfilePurchaseListSummaryInformation__Row">
                <div className="ProfilePurchaseListSummaryInformation__LabelCol">
                  <span className="ProfilePurchaseListSummaryInformation__Label">{textModifiedBy}</span>
                </div>
                <div className="ProfilePurchaseListSummaryInformation__ValueCol">
                  <span className="ProfilePurchaseListSummaryInformation__Value">{`${lastModifiedBy.firstName} ${lastModifiedBy.lastName}`}</span>
                </div>
              </div>
            </>
          )}
          {/* Purchase list description */}
          <div className="ProfilePurchaseListSummaryInformation__Row">
            <div className="ProfilePurchaseListSummaryInformation__TextCol">
              <span className="ProfilePurchaseListSummaryInformation__Text">{description}</span>
            </div>
          </div>

          {/* Purchase list sharing information */}
          {isListOwner && sharedWithInfo}
        </div>
      )}
    </Styled>
  );
};

export default ProfilePurchaseListSummaryInformation;
