import Card from '@oracle-cx-commerce/react-components/card';
import Link from '@oracle-cx-commerce/react-components/link';
import {PAGE_PURCHASE_LISTS_LINK} from '@oracle-cx-commerce/commerce-utils/constants';
import PropTypes from 'prop-types';
import React from 'react';
import Styled from '@oracle-cx-commerce/react-components/styled';
import UserIcon from '@oracle-cx-commerce/react-components/icons/user';
import UsersIcon from '@oracle-cx-commerce/react-components/icons/users';
import css from '@oracle-cx-commerce/react-widgets/profile/profile-purchase-lists-summary/components/purchase-lists-details/styles.css';
//constants
const PURCHASE_LISTS = PAGE_PURCHASE_LISTS_LINK;

/**
 * Widget to display the purchase lists summary details at profile page.
 * @param {Object} props the properties object
 */
const PurchaseListsDetails = props => {
  const {actionManage, textListsIveCreated, textListsSharedWithMe} = props;
  const {purchaseLists, sharedPurchaseLists} = props;

  return (
    <Styled id="PurchaseListsDetails" css={css}>
      <div className="PurchaseListsDetails__Card">
        <Card>
          <div className="PurchaseListsDetails__Lists">
            <div className="PurchaseListsDetails__RowItem">
              <div className="PurchaseListsDetails__Icon">
                <UserIcon />
              </div>
              <div className="PurchaseListsDetails__Content">
                <span className="PurchaseListsDetails__TotalList">{purchaseLists}</span> {textListsIveCreated}
              </div>
            </div>
            <div className="PurchaseListsDetails__RowItem">
              <div className="PurchaseListsDetails__Icon">
                <UsersIcon />
              </div>
              <div className="PurchaseListsDetails__Content">
                <span className="PurchaseListsDetails__TotalList">{sharedPurchaseLists}</span> {textListsSharedWithMe}
              </div>
            </div>
          </div>
          <div className="PurchaseListsDetails__Manage">
            <Link href={PURCHASE_LISTS}>{actionManage}</Link>
          </div>
        </Card>
      </div>
    </Styled>
  );
};

PurchaseListsDetails.propTypes = {
  /** Total number of purchase lists */
  purchaseLists: PropTypes.number,

  /**
   * Total number of shared purchase lists
   */
  sharedPurchaseLists: PropTypes.number
};

PurchaseListsDetails.defaultProps = {
  purchaseLists: 0,
  sharedPurchaseLists: 0
};

export default PurchaseListsDetails;
