import React from 'react';
import Styled from '@oracle-cx-commerce/react-components/styled';
import {useDateFormatter} from '@oracle-cx-commerce/react-components/utils/hooks';
import css from '@oracle-cx-commerce/react-widgets/profile/profile-quote-notes-history/components/quote-note/styles.css';
// import PropTypes from 'prop-types';

/**
 * Component to display a note for request quote notes history.
 * @param {Object} props the properties object
 */
const QuoteNote = props => {
  const {currentProfile, textMerchant, note = {requesterNote: '', providerNote: ''}} = props;
  const formatDate = useDateFormatter();

  return (
    <Styled id="QuoteNote" css={css}>
      <div className="QuoteNote">
        {note.requesterNote && (
          <div className="QuoteNote__requester">
            <div className="QuoteNote__author">{`${currentProfile.firstName} ${currentProfile.lastName}`}</div>
            <div className="QuoteNote__note">{note.requesterNote}</div>
            <div className="QuoteNote__date">{formatDate(new Date(note.requestDate))}</div>
          </div>
        )}
        {note.providerNote && (
          <div className="QuoteNote__provider">
            <div className="QuoteNote__author">{textMerchant}</div>
            <div className="QuoteNote__note">{note.providerNote}</div>
            <div className="QuoteNote__date">{formatDate(new Date(note.quoteDate))}</div>
          </div>
        )}
      </div>
    </Styled>
  );
};

QuoteNote.propTypes = {};

QuoteNote.defaultProps = {};

export default React.memo(QuoteNote);
