import {getPage, getScheduledOrderExecutionStatus} from '@oracle-cx-commerce/commerce-utils/selector';

export const getComponentData = state => {
  const scheduledOrderId = getPage(state).contextId;
  const executionStatuses = getScheduledOrderExecutionStatus(state, {id: scheduledOrderId});

  return {
    executionStatuses
  };
};
