import Card from '@oracle-cx-commerce/react-components/card';
import InfoCircle from '@oracle-cx-commerce/react-components/icons/info-circle';
import Link from '@oracle-cx-commerce/react-components/link';
import {PAGE_CONTACT_REGISTRATION_REQUESTS_LINK} from '@oracle-cx-commerce/commerce-utils/constants';
import PlusCircle from '@oracle-cx-commerce/react-components/icons/plus-circle';
import PropTypes from 'prop-types';
import QuestionCircle from '@oracle-cx-commerce/react-components/icons/question-circle';
/*
 ** Copyright (c) 2020 Oracle and/or its affiliates.
 */
import React from 'react';
import Styled from '@oracle-cx-commerce/react-components/styled';
import css from '@oracle-cx-commerce/react-widgets/profile/profile-registration-requests-summary/components/registration-requests-details/styles.css';

/**
 * Widget to display the registration requests summary details at profile page.
 * @param {Object} props the properties object
 */
const RegistrationRequestsDetails = props => {
  const {actionManage, textNewRequests, textRequestsInReview, textRequestsRequireMoreInfo} = props;
  const {newRegistrationRequests, registrationRequestsInReview, registrationRequestsRequireMoreInfo} = props;

  return (
    <Styled id="RegistrationRequestsDetails" css={css}>
      <div className="RegistrationRequestsDetails__Card">
        <Card>
          <div className="RegistrationRequestsDetails__RequestDetails">
            <div className="RegistrationRequestsDetails__RowItem">
              <div className="RegistrationRequestsDetails__Icon">
                <PlusCircle />
              </div>
              <div className="RegistrationRequestsDetails__Content">
                <span className="RegistrationRequestsDetails__RequestsCount">{newRegistrationRequests}</span>{' '}
                {textNewRequests}
              </div>
            </div>
            <div className="RegistrationRequestsDetails__RowItem">
              <div className="RegistrationRequestsDetails__Icon">
                <QuestionCircle />
              </div>
              <div className="RegistrationRequestsDetails__Content">
                <span className="RegistrationRequestsDetails__RequestsCount">{registrationRequestsInReview}</span>{' '}
                {textRequestsInReview}
              </div>
            </div>
            <div className="RegistrationRequestsDetails__RowItem">
              <div className="RegistrationRequestsDetails__Icon">
                <InfoCircle />
              </div>
              <div className="RegistrationRequestsDetails__Content">
                <span className="RegistrationRequestsDetails__RequestsCount">
                  {registrationRequestsRequireMoreInfo}
                </span>{' '}
                {textRequestsRequireMoreInfo}
              </div>
            </div>
          </div>
          <div className="RegistrationRequestsDetails__Manage">
            <Link href={PAGE_CONTACT_REGISTRATION_REQUESTS_LINK}>{actionManage}</Link>
          </div>
        </Card>
      </div>
    </Styled>
  );
};

RegistrationRequestsDetails.propTypes = {
  /** Number of registration require more info */
  registrationRequestsRequireMoreInfo: PropTypes.number,

  /** Number of new registration requests */
  newRegistrationRequests: PropTypes.number,

  /** Number of registration requests in review */
  registrationRequestsInReview: PropTypes.number
};

RegistrationRequestsDetails.defaultProps = {
  registrationRequestsRequireMoreInfo: 0,
  newRegistrationRequests: 0,
  registrationRequestsInReview: 0
};

export default RegistrationRequestsDetails;
