import {getCurrentOrganizationId, getRolesForProfile} from '@oracle-cx-commerce/commerce-utils/selector';

export const getComponentData = state => {
  const currentOrganizationId = getCurrentOrganizationId(state);
  const roles = getRolesForProfile(state);

  return {
    currentOrganizationId,
    roles
  };
};
