import React, {useContext} from 'react';

import Link from '@oracle-cx-commerce/react-components/link';
import {PAGE_RETURN_DETAILS_LINK} from '@oracle-cx-commerce/commerce-utils/constants';
import {ReturnContext} from '@oracle-cx-commerce/react-widgets/contexts';
import Styled from '@oracle-cx-commerce/react-components/styled';
import css from '@oracle-cx-commerce/react-widgets/profile/profile-return-details-link/styles.css';

/**
 * Displays the link for navigating to the return details of a return request
 */

const ProfileReturnDetailsLink = props => {
  const {returnDetails = {}} = useContext(ReturnContext);
  const {actionViewReturnDetails} = props;

  return (
    <Styled id="ProfileReturnDetailsLink" css={css}>
      <Link className="ProfileReturnDetailsLink" href={`${PAGE_RETURN_DETAILS_LINK}/${returnDetails.returnRequestId}`}>
        {actionViewReturnDetails}
      </Link>
    </Styled>
  );
};

export default ProfileReturnDetailsLink;
