import {ReturnContext} from '@oracle-cx-commerce/react-widgets/contexts';
import React, {useContext} from 'react';
import {t} from '@oracle-cx-commerce/utils/generic';
import {useDateFormatter} from '@oracle-cx-commerce/react-components/utils/hooks';

/**
 * Displays the creation date of a return request
 */
const ProfileReturnRequestDate = props => {
  const {returnDetails = {}} = useContext(ReturnContext);
  const {labelRequestOn} = props;
  const formatDate = useDateFormatter();

  return returnDetails.createdDate ? (
    <div>{t(labelRequestOn, {returnDate: formatDate(new Date(returnDetails.createdDate))})}</div>
  ) : null;
};

export default ProfileReturnRequestDate;
