import {ReturnContext} from '@oracle-cx-commerce/react-widgets/contexts';
import React, {useContext} from 'react';
import {t} from '@oracle-cx-commerce/utils/generic';

/**
 * Displays the id of a return request
 */
const ProfileReturnRequestId = props => {
  const {returnDetails = {}} = useContext(ReturnContext);
  const {labelReturnRequest} = props;

  return <h2>{t(labelReturnRequest, {returnRequestId: returnDetails.returnRequestId})}</h2>;
};

export default ProfileReturnRequestId;
