import React, {useContext} from 'react';

import Alert from '@oracle-cx-commerce/react-components/alert';
import {ReturnContext} from '@oracle-cx-commerce/react-widgets/contexts';
import Styled from '@oracle-cx-commerce/react-components/styled';
import css from '@oracle-cx-commerce/react-widgets/profile/profile-return-request-message/styles.css';

/**
 * Displays the refund message (complete/pending) of a return request
 */
const ProfileReturnRequestMessage = props => {
  const {returnDetails = {}} = useContext(ReturnContext);
  const {labelRefundCompleteMessage, labelRefundPendingMessage} = props;
  const returnStatus = returnDetails.returnRequestStatus || '';
  const statusComplete = 'COMPLETE';
  const refundMessage =
    returnStatus.toUpperCase() === statusComplete ? labelRefundCompleteMessage : labelRefundPendingMessage;

  return (
    <Styled id="ProfileReturnRequestMessage" css={css}>
      <div className="ProfileReturnRequestMessage">
        <Alert type="info" message={refundMessage} />
      </div>
    </Styled>
  );
};

export default ProfileReturnRequestMessage;
