import {ReturnContext} from '@oracle-cx-commerce/react-widgets/contexts';
import Badge from '@oracle-cx-commerce/react-components/badge';
import React, {useContext} from 'react';
import Styled from '@oracle-cx-commerce/react-components/styled';
import css from '@oracle-cx-commerce/react-widgets/profile/profile-return-request-status/styles.css';

/**
 * Displays the status of a return request
 */
const ProfileReturnRequestStatus = props => {
  const {returnDetails = {}} = useContext(ReturnContext);
  const {labelReturnRequestStatus} = props;

  return (
    <Styled id="ProfileReturnRequestStatus" css={css}>
      <div className="ProfileReturnRequestStatus">
        <span className="ProfileReturnRequestStatus__Label">{labelReturnRequestStatus}</span>
        <Badge badgeText={returnDetails.returnRequestStatus} />
      </div>
    </Styled>
  );
};

export default ProfileReturnRequestStatus;
