import {useSelector} from '@oracle-cx-commerce/react-components/provider';
import {getWishList, getWishlists, access} from '@oracle-cx-commerce/commerce-utils/selector/wishlists';
import {getPage} from '@oracle-cx-commerce/commerce-utils/selector';

/**
 * Hook to fetch the wishlist data for the component
 */

export const useComponentData = () => {
  const wishListId = useSelector(getPage).contextId;
  const wishList = useSelector(getWishList, {id: wishListId});
  const wishLists = useSelector(getWishlists);
  const {userId} = useSelector(access);

  return {
    wishListId,
    wishList,
    wishLists,
    userId
  };
};
