import {access, getWishlists} from '@oracle-cx-commerce/commerce-utils/selector/wishlists';

export const getComponentData = state => {
  const {siteId, userId} = access(state);
  const wishlists = getWishlists(state);

  return {
    siteId,
    userId,
    wishlists
  };
};
