import {getScheduledOrder, getOrder, getPage} from '@oracle-cx-commerce/commerce-utils/selector';

export const getComponentData = state => {
  const scheduledOrderId = getPage(state).contextId;
  const scheduledOrder = getScheduledOrder(state, {id: scheduledOrderId});
  const templateOrderId = scheduledOrder !== undefined ? scheduledOrder.templateOrderId : '';
  const order = getOrder(state, {id: templateOrderId});

  return {
    scheduledOrderId,
    scheduledOrder,
    order
  };
};
