import {getSite, getVisitorId} from '@oracle-cx-commerce/commerce-utils/selector';

export const getAPMSettings = state => getSite(state).siteSettings || {};

export const getSiteName = state => getSite(state).name || '';

export const getApmUserName = state => {
  const vistorId = getVisitorId(state);
  if (!vistorId) {
    return 'unknown';
  }

  return vistorId;
};
