export default ({endpoint, subscribeDispatch}) => {
  return subscribeDispatch((action, state) => {
    const {type} = action;
    if (type === 'getApplicationPage') {
      endpoint('savePageViewsCount', {pageViews: 1}, state);
    }
  });
};
