const {loadWorkspaceConfig} = require('@oracle-cx-commerce/cli-shared/config/config-loader');

const readConfig = (appName = process.env.OCC_APP_NAME || 'blank-store', opts = {}) => {
  const options = loadWorkspaceConfig(appName, opts);

  return options;
};

export default readConfig;
