const locales = require('../../locales');

test.each([
  '',
  ' ',
  '   ',
  'en-ZZ',
  'pp',
  'pp-ZZ',
  'en_US',
  'en-US.UTF-8',
  'eng-US',
  'en-USA',
  'eng-USA',
  '-US',
  'en-'
])('Invalid locale codes are rejected', locale => {
  expect(locales.validate(locale)).toBe(false);
});

test.each(['en-US', 'en', 'eo', 'eo-US', 'en-AU'])('Valid locale codes are approved', locale => {
  expect(locales.validate(locale)).toBe(true);
});
